targets.expr:
pg_stat_user_tables_n_dead_tup{server="$pg_master", relname=~"$focus_table", datname=~"$database"} /
(
    (pg_stat_user_tables_n_live_tup{server="$pg_master", relname=~"$focus_table", datname=~"$database"} *
     ignoring (datname,relname,schemaname) group_left (server) pg_settings_autovacuum_vacuum_scale_factor{server="$pg_master"}
    ) + ignoring (datname,relname,schemaname) group_left (server) pg_settings_autovacuum_vacuum_threshold{server="$pg_master"}
) * 100
